package main.carimport main.Transmissionimport main.detail.Electrocityimport main.detail.Engineimport main.detail.GazTankimport main.detail.Headlightsimport main.detail.Wheelimport java.math.BigDecimal//Для грузового фургона есть дополнительный параметр - грузоподъемность (количество килограмм)abstract class Truck : Car{    var velocity: Int    constructor(        color: String,        maxSpeed: Int,        transmission: Transmission,        isMoving: Boolean,        wheels: Array<Wheel>,        gazTank: GazTank,        engine: Engine,        electrocity: Electrocity,        headlights: Headlights,        price: BigDecimal,        velocity: Int    ) : super(color, maxSpeed, transmission, isMoving, wheels, gazTank, engine, electrocity, headlights, price) {        this.velocity = velocity    }}